mapper.insert:
insert into account (ID, UID, MONEY
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{money,jdbcType=DOUBLE}
  )

mapper.update:
update account
set UID = #{uid,jdbcType=INTEGER},
  MONEY = #{money,jdbcType=DOUBLE}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, UID, MONEY
from account
where ID = #{id,jdbcType=INTEGER}
